package com.example._207103_project;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.scene.Parent;
import javafx.scene.Node;
import javafx.scene.control.TextArea;
import javafx.scene.control.TextField;
import javafx.scene.image.Image;
import javafx.scene.image.ImageView;
import javafx.stage.FileChooser;
import javafx.stage.Stage;
import java.io.File;
public class CreateController {
    @FXML private TextField name;
    @FXML private TextField email;
    @FXML private TextField phone;
    @FXML private TextField address;
    @FXML private TextArea education;
    @FXML private TextArea skill;
    @FXML private TextArea project;
    @FXML private TextArea work;
    @FXML private ImageView photoView;
    private String photoPath;
    private CVModel editingCV;
    private boolean isEditMode = false;
    @FXML
    private void onChoosePhoto(ActionEvent event) throws Exception {
        FileChooser fc = new FileChooser();
        fc.getExtensionFilters().addAll(new FileChooser.ExtensionFilter("Image Files","*.png","*.jpg","*.jpeg","*.gif"));
        File file = fc.showOpenDialog(((Node)event.getSource()).getScene().getWindow());
        if(file!=null){
            photoPath = file.toURI().toString();
            photoView.setImage(new Image(photoPath,120,120,true,true));
        }
    }
    public void loadCVForEdit(CVModel cv) {
        this.editingCV = cv;
        this.isEditMode = true;
        name.setText(cv.getFullName());
        email.setText(cv.getEmail());
        phone.setText(cv.getPhone());
        address.setText(cv.getAddress());
        if (cv.getPhotoPath() != null && !cv.getPhotoPath().isEmpty()) {
            photoPath = cv.getPhotoPath();
            try {
                photoView.setImage(new Image(photoPath, 120, 120, true, true));
            } catch (Exception e) {
            }
        }
        education.setText(String.join("\n", cv.getEducation()));
        skill.setText(String.join(", ", cv.getSkills()));
        project.setText(String.join("\n", cv.getProjects()));
        work.setText(String.join("\n", cv.getExperience()));
    }
    @FXML
    private void backToWelcome(ActionEvent event) throws Exception {
        Parent root = FXMLLoader.load(getClass().getResource("/WelcomePage.fxml"));
        Stage stage = (Stage)((Node)event.getSource()).getScene().getWindow();
        stage.getScene().setRoot(root);
    }
    @FXML
    private void onGenerate(ActionEvent event) throws Exception {
        CVModel model;
        if (isEditMode && editingCV != null) {
            model = editingCV;
        } else {
            model = new CVModel();
        }
        model.setFullName(text(name.getText()));
        model.setEmail(text(email.getText()));
        model.setPhone(text(phone.getText()));
        model.setAddress(text(address.getText()));
        model.setPhotoPath(photoPath==null?"":photoPath);
        model.getEducation().clear();
        for(String s: splitLines(education.getText())) if(!s.isBlank()) model.getEducation().add(s.trim());
        model.getSkills().clear();
        for(String s: splitCommaOrLines(skill.getText())) if(!s.isBlank()) model.getSkills().add(s.trim());
        model.getProjects().clear();
        for(String s: splitLines(project.getText())) if(!s.isBlank()) model.getProjects().add(s.trim());
        model.getExperience().clear();
        for(String s: splitLines(work.getText())) if(!s.isBlank()) model.getExperience().add(s.trim());
        FXMLLoader loader = new FXMLLoader(getClass().getResource("/preview.fxml"));
        Parent root = loader.load();
        PreviewController pc = loader.getController();
        pc.setModel(model);
        pc.setEditMode(isEditMode);
        Stage stage = (Stage)((Node)event.getSource()).getScene().getWindow();
        stage.getScene().setRoot(root);
    }
    private String text(String s){ return s==null?"":s.trim(); }
    private String[] splitLines(String s){ return s==null||s.isBlank()?new String[0]:s.split("\\r?\\n"); }
    private String[] splitCommaOrLines(String s){ return s==null||s.isBlank()?new String[0]:s.split("[,\\r?\\n]+"); }
}

package com.example._207103_project;
import java.sql.*;
import java.util.ArrayList;
import java.util.List;
public class DatabaseManager {
    private static final String DB_URL = "jdbc:sqlite:cv_database.db";
    private static DatabaseManager instance;
    private DatabaseManager() {
        initializeDatabase();
    }
    public static DatabaseManager getInstance() {
        if (instance == null) {
            instance = new DatabaseManager();
        }
        return instance;
    }
    private void initializeDatabase() {
        try (Connection conn = DriverManager.getConnection(DB_URL);
             Statement stmt = conn.createStatement()) {
            stmt.execute("PRAGMA foreign_keys = ON");
            String createCVTable = """
                CREATE TABLE IF NOT EXISTS cv (
                    id INTEGER PRIMARY KEY AUTOINCREMENT,
                    full_name TEXT NOT NULL,
                    email TEXT,
                    phone TEXT,
                    address TEXT,
                    photo_path TEXT,
                    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
                )
                """;
            stmt.execute(createCVTable);
            String createEducationTable = """
                CREATE TABLE IF NOT EXISTS education (
                    id INTEGER PRIMARY KEY AUTOINCREMENT,
                    cv_id INTEGER NOT NULL,
                    entry TEXT NOT NULL,
                    FOREIGN KEY (cv_id) REFERENCES cv(id) ON DELETE CASCADE
                )
                """;
            stmt.execute(createEducationTable);
            String createSkillsTable = """
                CREATE TABLE IF NOT EXISTS skills (
                    id INTEGER PRIMARY KEY AUTOINCREMENT,
                    cv_id INTEGER NOT NULL,
                    skill TEXT NOT NULL,
                    FOREIGN KEY (cv_id) REFERENCES cv(id) ON DELETE CASCADE
                )
                """;
            stmt.execute(createSkillsTable);
            String createExperienceTable = """
                CREATE TABLE IF NOT EXISTS experience (
                    id INTEGER PRIMARY KEY AUTOINCREMENT,
                    cv_id INTEGER NOT NULL,
                    entry TEXT NOT NULL,
                    FOREIGN KEY (cv_id) REFERENCES cv(id) ON DELETE CASCADE
                )
                """;
            stmt.execute(createExperienceTable);
            String createProjectsTable = """
                CREATE TABLE IF NOT EXISTS projects (
                    id INTEGER PRIMARY KEY AUTOINCREMENT,
                    cv_id INTEGER NOT NULL,
                    project TEXT NOT NULL,
                    FOREIGN KEY (cv_id) REFERENCES cv(id) ON DELETE CASCADE
                )
                """;
            stmt.execute(createProjectsTable);
        } catch (SQLException e) {
        }
    }
    public int saveCV(CVModel cv) {
        String insertCV = "INSERT INTO cv (full_name, email, phone, address, photo_path) VALUES (?, ?, ?, ?, ?)";
        try (Connection conn = DriverManager.getConnection(DB_URL)) {
            conn.setAutoCommit(false);
            try (PreparedStatement pstmt = conn.prepareStatement(insertCV, Statement.RETURN_GENERATED_KEYS)) {
                pstmt.setString(1, cv.getFullName());
                pstmt.setString(2, cv.getEmail());
                pstmt.setString(3, cv.getPhone());
                pstmt.setString(4, cv.getAddress());
                pstmt.setString(5, cv.getPhotoPath());
                pstmt.executeUpdate();
                ResultSet rs = pstmt.getGeneratedKeys();
                if (rs.next()) {
                    int cvId = rs.getInt(1);
                    cv.setId(cvId);
                    saveListEntries(conn, cvId, cv.getEducation(), "education", "entry");
                    saveListEntries(conn, cvId, cv.getSkills(), "skills", "skill");
                    saveListEntries(conn, cvId, cv.getExperience(), "experience", "entry");
                    saveListEntries(conn, cvId, cv.getProjects(), "projects", "project");
                    conn.commit();
                    return cvId;
                }
            } catch (SQLException e) {
                conn.rollback();
                throw e;
            }
        } catch (SQLException e) {
        }
        return -1;
    }
    private void saveListEntries(Connection conn, int cvId, List<String> entries, String tableName, String columnName) throws SQLException {
        if (entries == null || entries.isEmpty()) return;
        String sql = "INSERT INTO " + tableName + " (cv_id, " + columnName + ") VALUES (?, ?)";
        try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
            for (String entry : entries) {
                if (entry != null && !entry.isBlank()) {
                    pstmt.setInt(1, cvId);
                    pstmt.setString(2, entry);
                    pstmt.executeUpdate();
                }
            }
        }
    }
    public List<CVModel> getAllCVs() {
        List<CVModel> cvList = new ArrayList<>();
        String query = "SELECT id, full_name, email, phone, address, photo_path FROM cv ORDER BY created_at DESC";
        try (Connection conn = DriverManager.getConnection(DB_URL);
             Statement stmt = conn.createStatement();
             ResultSet rs = stmt.executeQuery(query)) {
            while (rs.next()) {
                CVModel cv = new CVModel();
                cv.setId(rs.getInt("id"));
                cv.setFullName(rs.getString("full_name"));
                cv.setEmail(rs.getString("email"));
                cv.setPhone(rs.getString("phone"));
                cv.setAddress(rs.getString("address"));
                cv.setPhotoPath(rs.getString("photo_path"));
                cvList.add(cv);
            }
        } catch (SQLException e) {
        }
        return cvList;
    }
    public CVModel getCVById(int id) {
        String query = "SELECT id, full_name, email, phone, address, photo_path FROM cv WHERE id = ?";
        try (Connection conn = DriverManager.getConnection(DB_URL);
             PreparedStatement pstmt = conn.prepareStatement(query)) {
            pstmt.setInt(1, id);
            ResultSet rs = pstmt.executeQuery();
            if (rs.next()) {
                CVModel cv = new CVModel();
                cv.setId(rs.getInt("id"));
                cv.setFullName(rs.getString("full_name"));
                cv.setEmail(rs.getString("email"));
                cv.setPhone(rs.getString("phone"));
                cv.setAddress(rs.getString("address"));
                cv.setPhotoPath(rs.getString("photo_path"));
                cv.getEducation().addAll(loadListEntries(id, "education", "entry"));
                cv.getSkills().addAll(loadListEntries(id, "skills", "skill"));
                cv.getExperience().addAll(loadListEntries(id, "experience", "entry"));
                cv.getProjects().addAll(loadListEntries(id, "projects", "project"));
                return cv;
            }
        } catch (SQLException e) {
        }
        return null;
    }
    private List<String> loadListEntries(int cvId, String tableName, String columnName) throws SQLException {
        List<String> entries = new ArrayList<>();
        String query = "SELECT " + columnName + " FROM " + tableName + " WHERE cv_id = ?";
        try (Connection conn = DriverManager.getConnection(DB_URL);
             PreparedStatement pstmt = conn.prepareStatement(query)) {
            pstmt.setInt(1, cvId);
            ResultSet rs = pstmt.executeQuery();
            while (rs.next()) {
                entries.add(rs.getString(columnName));
            }
        }
        return entries;
    }
    public void updateCV(CVModel cv) {
        String updateCVQuery = "UPDATE cv SET full_name = ?, email = ?, phone = ?, address = ?, photo_path = ? WHERE id = ?";
        try (Connection conn = DriverManager.getConnection(DB_URL)) {
            conn.setAutoCommit(false);
            try {
                try (Statement stmt = conn.createStatement()) {
                    stmt.execute("PRAGMA foreign_keys = ON");
                }
                try (PreparedStatement pstmt = conn.prepareStatement(updateCVQuery)) {
                    pstmt.setString(1, cv.getFullName());
                    pstmt.setString(2, cv.getEmail());
                    pstmt.setString(3, cv.getPhone());
                    pstmt.setString(4, cv.getAddress());
                    pstmt.setString(5, cv.getPhotoPath());
                    pstmt.setInt(6, cv.getId());
                    pstmt.executeUpdate();
                }
                deleteListEntries(conn, cv.getId(), "education");
                deleteListEntries(conn, cv.getId(), "skills");
                deleteListEntries(conn, cv.getId(), "experience");
                deleteListEntries(conn, cv.getId(), "projects");
                saveListEntries(conn, cv.getId(), cv.getEducation(), "education", "entry");
                saveListEntries(conn, cv.getId(), cv.getSkills(), "skills", "skill");
                saveListEntries(conn, cv.getId(), cv.getExperience(), "experience", "entry");
                saveListEntries(conn, cv.getId(), cv.getProjects(), "projects", "project");
                conn.commit();
            } catch (SQLException e) {
                conn.rollback();
                throw e;
            }
        } catch (SQLException e) {
        }
    }
    private void deleteListEntries(Connection conn, int cvId, String tableName) throws SQLException {
        String sql = "DELETE FROM " + tableName + " WHERE cv_id = ?";
        try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setInt(1, cvId);
            pstmt.executeUpdate();
        }
    }
    public void deleteCV(int id) {
        String deleteCV = "DELETE FROM cv WHERE id = ?";
        try (Connection conn = DriverManager.getConnection(DB_URL)) {
            try (Statement stmt = conn.createStatement()) {
                stmt.execute("PRAGMA foreign_keys = ON");
            }
            try (PreparedStatement pstmt = conn.prepareStatement(deleteCV)) {
                pstmt.setInt(1, id);
                pstmt.executeUpdate();
            }
        } catch (SQLException e) {
        }
    }
}

package com.example._207103_project;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.geometry.Insets;
import javafx.scene.Node;
import javafx.scene.Parent;
import javafx.scene.control.*;
import javafx.scene.layout.HBox;
import javafx.scene.layout.Priority;
import javafx.scene.layout.Region;
import javafx.scene.layout.VBox;
import javafx.stage.Stage;
import java.util.List;
import java.util.Optional;
public class ExploreController {
    @FXML private VBox cvListContainer;
    @FXML
    public void initialize() {
        loadSavedCVs();
    }
    private void loadSavedCVs() {
        cvListContainer.getChildren().clear();
        DatabaseManager dbManager = DatabaseManager.getInstance();
        List<CVModel> cvList = dbManager.getAllCVs();
        if (cvList.isEmpty()) {
            Label emptyLabel = new Label("No saved CVs found. Create one first!");
            emptyLabel.setStyle("-fx-font-size:16px; -fx-text-fill:#64748b; -fx-padding:20;");
            cvListContainer.getChildren().add(emptyLabel);
        } else {
            for (CVModel cv : cvList) {
                cvListContainer.getChildren().add(createCVCard(cv));
            }
        }
    }
    private HBox createCVCard(CVModel cv) {
        HBox card = new HBox(12);
        card.setStyle("-fx-background-color: white; -fx-background-radius: 8; " +
                      "-fx-effect: dropshadow(gaussian, rgba(0,0,0,0.1), 8,0,0,2); " +
                      "-fx-padding: 16;");
        card.setPrefHeight(Region.USE_COMPUTED_SIZE);
        VBox infoBox = new VBox(6);
        Label nameLabel = new Label(cv.getFullName());
        nameLabel.setStyle("-fx-font-size:18px; -fx-font-weight:bold; -fx-text-fill:#0b3a4a;");
        Label emailLabel = new Label(cv.getEmail() != null ? cv.getEmail() : "");
        emailLabel.setStyle("-fx-font-size:13px; -fx-text-fill:#64748b;");
        Label phoneLabel = new Label(cv.getPhone() != null ? cv.getPhone() : "");
        phoneLabel.setStyle("-fx-font-size:13px; -fx-text-fill:#64748b;");
        infoBox.getChildren().addAll(nameLabel, emailLabel, phoneLabel);
        HBox.setHgrow(infoBox, Priority.ALWAYS);
        VBox buttonBox = new VBox(8);
        buttonBox.setStyle("-fx-alignment: center-right;");
        Button viewBtn = new Button("View");
        viewBtn.setStyle("-fx-background-color:#3b82f6; -fx-text-fill:white; " +
                        "-fx-font-weight:bold; -fx-background-radius:6; -fx-padding:6 16 6 16;");
        viewBtn.setOnAction(_ -> viewCV(cv.getId()));
        Button editBtn = new Button("Edit");
        editBtn.setStyle("-fx-background-color:#10b981; -fx-text-fill:white; " +
                        "-fx-font-weight:bold; -fx-background-radius:6; -fx-padding:6 16 6 16;");
        editBtn.setOnAction(_ -> editCV(cv.getId()));
        Button deleteBtn = new Button("Delete");
        deleteBtn.setStyle("-fx-background-color:#ef4444; -fx-text-fill:white; " +
                          "-fx-font-weight:bold; -fx-background-radius:6; -fx-padding:6 16 6 16;");
        deleteBtn.setOnAction(_ -> deleteCV(cv.getId()));
        buttonBox.getChildren().addAll(viewBtn, editBtn, deleteBtn);
        card.getChildren().addAll(infoBox, buttonBox);
        VBox.setMargin(card, new Insets(0, 0, 12, 0));
        return card;
    }
    private void viewCV(int cvId) {
        try {
            DatabaseManager dbManager = DatabaseManager.getInstance();
            CVModel cv = dbManager.getCVById(cvId);
            if (cv != null) {
                FXMLLoader loader = new FXMLLoader(getClass().getResource("/preview.fxml"));
                Parent root = loader.load();
                PreviewController pc = loader.getController();
                pc.setModel(cv);
                Stage stage = (Stage) cvListContainer.getScene().getWindow();
                stage.getScene().setRoot(root);
            }
        } catch (Exception e) {
            showError("Failed to load CV: " + e.getMessage());
        }
    }
    private void editCV(int cvId) {
        try {
            DatabaseManager dbManager = DatabaseManager.getInstance();
            CVModel cv = dbManager.getCVById(cvId);
            if (cv != null) {
                FXMLLoader loader = new FXMLLoader(getClass().getResource("/create.fxml"));
                Parent root = loader.load();
                CreateController controller = loader.getController();
                controller.loadCVForEdit(cv);
                Stage stage = (Stage) cvListContainer.getScene().getWindow();
                stage.getScene().setRoot(root);
            }
        } catch (Exception e) {
            showError("Failed to load CV for editing: " + e.getMessage());
        }
    }
    private void deleteCV(int cvId) {
        Alert confirmAlert = new Alert(Alert.AlertType.CONFIRMATION);
        confirmAlert.setTitle("Confirm Delete");
        confirmAlert.setHeaderText(null);
        confirmAlert.setContentText("Are you sure you want to delete this CV?");
        Optional<ButtonType> result = confirmAlert.showAndWait();
        if (result.isPresent() && result.get() == ButtonType.OK) {
            try {
                DatabaseManager dbManager = DatabaseManager.getInstance();
                dbManager.deleteCV(cvId);
                Alert successAlert = new Alert(Alert.AlertType.INFORMATION);
                successAlert.setTitle("Success");
                successAlert.setHeaderText(null);
                successAlert.setContentText("CV deleted successfully!");
                successAlert.showAndWait();
                loadSavedCVs();
            } catch (Exception e) {
                showError("Failed to delete CV: " + e.getMessage());
            }
        }
    }
    @FXML
    private void backToWelcome(ActionEvent event) {
        try {
            var resource = getClass().getResource("/WelcomePage.fxml");
            if (resource == null) {
                showError("WelcomePage.fxml not found");
                return;
            }
            Parent root = FXMLLoader.load(resource);
            Stage stage = (Stage) ((Node) event.getSource()).getScene().getWindow();
            stage.getScene().setRoot(root);
        } catch (Exception e) {
            showError("Failed to return to welcome page: " + e.getMessage());
        }
    }
    private void showError(String message) {
        Alert alert = new Alert(Alert.AlertType.ERROR);
        alert.setTitle("Error");
        alert.setHeaderText(null);
        alert.setContentText(message);
        alert.showAndWait();
    }
}

